const VoteScore = ({video, setListOfVideos}) => {
      const handleUpVote = () => {
        const updatedVideos = [...setListOfVideos];
        const index = updatedVideos.findIndex((v) => v.id === video.id);
        updatedVideos[index].rating += 1;
        setListOfVideos(updatedVideos);
      };

      const handleDownVote = () => {
        const updatedVideos = [...setListOfVideos];
        const index = updatedVideos.findIndex((v) => v.id === video.id);
        updatedVideos[index].rating -= 1;
        setListOfVideos(updatedVideos);
      };
    return (
      <div>
        <button onClick={() => handleVote("like")}>Up Vote</button>

        <button onClick={() => handleVote("dislike")}>Down Vote</button>

      </div>
    );
}

export default VoteScore;
