import "./App.css";
import React, { useEffect,useState } from "react";

function Uploadvideo() {
   const [videoData, setVideoData] = useState([]);
  const [submissionStatus, setSubmissionStatus] = useState(null);

  function isValidURL(url) {
    const pattern = /^(https?:\/\/)?[a-z0-9\-._~:/?#[\]@!$&'()*+,;=]+$/i;
    return pattern.test(url);
  }

  useEffect(() => {
    fetch("/videos")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const [title, setTitle] = useState("");
  const [url, setURL] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (title.trim() === "") {
      return alert("Title cannot be empty.");
    }
    // try {
    //   setSubmissionStatus("success");
    // } catch (error) {
    //   setSubmissionStatus("error");
    // }

    if (!isValidURL(url)) {
      return alert("Please enter a valid URL.");
    }
    // const currentDate = new Date();
    //const formattedDate = currentDate.toISOString();

    const formData = {
      title: title,
      rating: 0,
      url: url.split("watch?")[0] + "embed/" + url.split("?")[1],
    };

    try {
      const response = await fetch("/videos", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        console.log("Form data submitted successfully");
      } else {
        console.error("Form data submission failed");
      }
    } catch (error) {
      console.log("Error submitting form:", error);
    }
  };

  ///////

  const [showForm, setShowForm] = useState(false);

  const handleButtonClick = () => {
    if (!showForm) {
      setShowForm(true);
    }
  };
  const handleCancelButtonClick = () => {
    if (showForm) {
      setShowForm(false);
    }
  };

  return (
    <div>
      <button class="btn btn-light customBackground2" onClick={handleButtonClick}>
        Upload Video
      </button>
      {showForm && (
        <div class="container">
          {submissionStatus === "success" && (
            <p>Form submitted successfully!</p>
          )}
          {submissionStatus === "error" && (
            <p>There was an error submitting the form.</p>
          )}

          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="title">Title:</label>
              <input
                id="title"
                type="text"
                value={title}
                class="input"
                name="title"
                onChange={(e) => setTitle(e.target.value)}
                required
              ></input>
            </div>
            <div>
              <label htmlFor="url">url:&nbsp;&nbsp;&nbsp;</label>
              <input
                id="url"
                value={url}
                class="input"
                type="text"
                name="url"
                onChange={(e) => setURL(e.target.value)}
                required
              ></input>
            </div>
            <div class="formBtn">
              <button class="btn btn-light cancelBtn" onClick={handleCancelButtonClick}>
                Cancel
              </button>
              <button class="btn btn-light uploadBtn" type="submit">
                Upload
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
function Searchvideo() {
  return (
    <div>
      <label for="searchid">Search:</label>
      <input
        id="searchid"
        type="text"
        class="searchVideo"
        name="search"
      ></input>
    </div>
  );
}

export default function Search() {
  return (
    <div class="search">
      <Uploadvideo />
      <Searchvideo />
    </div>
  );
}
