import React from "react";

function ShowingVideos({ result, onRatingChange, onDeleteVideo }) {
  function convertToEmbedUrl(url) {
    const videoId = url.split("v=")[1];
    return `https://www.youtube.com/embed/${videoId}`;
  }

  const sortedVideos = [...result].sort((a, b) => b.rating - a.rating);

  return (
    <div className="ShowingVideos">
      {sortedVideos.map((video) => (
        <div className="videos" key={video.id}>
          <p>{video.title}</p>
          <div className="buttons">
            <i
              className="fa-solid fa-thumbs-up"
              onClick={() => onRatingChange(video.id, "thumbs-up")}
            ></i>
            <h4>{video.rating}</h4>
            <i
              className="fa-solid fa-thumbs-down"
              onClick={() => onRatingChange(video.id, "thumbs-down")}
            ></i>
          </div>

          <iframe
            className="allVideos"
            width="300"
            height="220"
            src={convertToEmbedUrl(video.url)}
            title={video.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
          <button onClick={() => onDeleteVideo(video.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default ShowingVideos;
