import React, {useState} from 'react';

const VideoItem = ({ video, onDelete }) => {
  const [videos, setVideos] = useState([]);
  const [rating, setRating] = useState();

  const handleUpVote = (videoToUpdate) => {

    // Update the vote count for the video
    const updatedVideosUpVote = videos.map((video) =>
      video.id === videoToUpdate.id
        ? { ...video, votes: video.votes + 1 }
        : video
    );
    setVideos(updatedVideosUpVote);
  };

  // Update the vote count for the video
  const handleDownVote = (videoToUpdate) => {
    const updatedVideosDownVote = videos.map((video) =>
      video.id === videoToUpdate.id
        ? { ...video, votes: Math.max(video.votes - 1, 0) }
        : video
    );
    setVideos(updatedVideosDownVote);
  };

  const videoId = video.url.split('v=')[1];

  return (
    <div className="video-item">  
      <iframe 
      width="560" 
      height="315" 
      src={`https://www.youtube.com/embed/${videoId}`} 
      title="YouTube video player" 
      // frameborder="0" 
      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
      // allowfullscreen
      >
      </iframe>
      <h5>{video.title}</h5>
      <p>Votes: {video.rating}</p>

      <i onClick={() => handleUpVote(video.id)} type='button' style={{ fontSize: '24px' }}  className="fa fa-thumbs-up"></i>
      <i onClick={() => handleDownVote(video.id)} type='button' style={{ fontSize: '24px' }}  className="fa fa-thumbs-down"></i>

      <button
      onClick={() => onDelete(video.id)}
      type='button'
      className='btn btn-danger'
      >
        Delete
      </button>
    </div>
  ); 
}



export default VideoItem;
