import React, {useState} from "react";
import "./compStyle.css"


export const Forms = ({callback}) => {


const [addVideo, setAddVideo] = useState(true)



const showNewSection = () => setAddVideo(true);
const hideNewSection = (e) => {
  e.preventDefault()
  setAddVideo(false)
}

const validateInput = (e) => {
  e.preventDefault()
  let url = document.getElementById("enterurl").value;
  let title = document.getElementById("title").value
  let p = /^(?:https?:\/\/)?(?:m\.|www\.)?(?:youtu\.be\/|youtube\.com\/(?:embed\/|v\/|watch\?v=|watch\?.+&v=))((\w|-){11})(?:\S+)?$/;
    if(url.match(p)){
        let addNewVideo = {
          url : `${url}`,
          title : `${title}`
        }
    }
    alert("Not a valid youtube url");
    return false;
}


   
  callback(addNewVideo)

 
    return (
  <form>
  <div className="m-5">
  <p className="text-primary cursoring" onClick={showNewSection}>Add Video</p>
  <div className= {addVideo ? "d-block" : "d-none"}>
    <div className="row mb-3">
      <label for="title" class="col-sm-1 col-form-label">Title</label>
      <div className="col-sm-3">
        <input type="text" class="form-control" id="title" placeholder="Enter Title"/>
      </div>
    </div>
    <div class="row mb-3">
      <label for="enterurl" class="col-sm-1 col-form-label">URL</label>
      <div class="col-sm-3">
        <input onChange type="url" class="form-control" id="enterurl" placeholder="Enter URL"/>
      </div>
    </div>        
    <div class="col-auto">
      <button onClick={hideNewSection} class="btn btn-primary">Clear</button>
      <button onClick={validateInput} type="submit" className="btn btn-primary ml-3">Add</button>
    </div>                 
  </div>
  </div>
  </form>
    )
}
