import React from 'react';
import data from "../data/exampleresponse.json";
import { useState } from 'react';
import Card from "./Card"
const Form = ({form}) => {
    const [show,setShow]=useState("");

// function showHandler(event){ 
//   event.preventDefault()
// }
function handlChange(e){
    const event=e.target.value
    const find= data.filter(item=>
        item.title.toLocaleLowerCase().includes(event.toLocaleLowerCase())
       )
    console.log("mm")
       setShow(find)    
}
    return (
        <div>
            <div  className="form-group" style={{display: form ? "inline" : "none"}}>
                <label >Title</label>
                <input  type="text"  placeholder="enter the title"  ></input>
                <br/>
                <label>URL</label>
                <input type="text" placeholder="enter the url"  ></input>
                <br/>
                <button onClick={handlChange} className="btn btn-success">ADD</button>
                <button  className="btn btn-danger">Cancel</button>
            </div>
            <Card  show={show}/>
            
        </div>
    );
}

export default Form;
