import React, { useState, useEffect } from "react";
import LikeButton from "./LikeButton";
import axios from "axios";

function VideosForm(props) {
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");
  const [rating, setRating] = useState("");
  const [videoList, setVideoList] = useState([]);
  // const [stateRating, setStateRating] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3001/api/get").then((response) => {
      setVideoList(response.data.rows);
    });
  }, []);

  function handleSubmit() {
    console
    axios.post("http://localhost:3001/api/insert", {
      newTitle: title,
      newUrl: url,
      newRating: rating,
    });

    setVideoList([
      ...videoList,
      { newTitle: title, newUrl: url, newRating: rating },
    ]);
  }

  function handleDelete(vid) {
    axios.delete(`http://localhost:3001/api/delete/${vid}`);
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Enter Video Title"
        id="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />

      <input
        type="url"
        placeholder="Enter Video Link"
        id="text"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
      />

      <input
        type="number"
        placeholder="Enter Rating"
        id="text"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
      />
      <button onClick={handleSubmit}>Add Video</button>

      {videoList.map((video) => (
        <div>
          <p>{video.title}</p>

          {/* <video width="320" height="240" src={link} controls/> */}

          <iframe
            src={video.url}
            title="YouTube video player"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            width="560"
            height="315"
          ></iframe>

          <h3> Rating: {video.rating}</h3>

          <LikeButton key={video.id} id={video.id} />

          <button
            onClick={() => {
              handleDelete(video.id);
            }}
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
}

