import React, { useState } from "react";

function AddVideo({ videos, setVideos }) {
  const [showValid, Setvalid] = useState(true);
    const [newVideo, setNewVideo] = useState({});
   const [videoTitle, setVideoTitle] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  //   const [, setVideos] = useState(videos);

  const handleShowValid = () => {
    Setvalid(!showValid);
  };
  const handleAddVideo = (event) => {
    event.preventDefault();
    videoTitle !== ""
      ? (newVideo.title = videoTitle)
      : alert("Please add a title for your video.");

    showValid(videoUrl)
      ? (newVideo.url = videoUrl)
      : alert("Please add a valid url.");
    newVideo.rating = Math.floor(Math.random() * 5000) + 1;

    const currentDate = new Date();
    const currentYear = currentDate.getFullYear();
    const currentMonth = currentDate.getMonth() + 1; // add 1 because getMonth() returns 0-11
    const currentDay = currentDate.getDate();
    newVideo.date = currentYear + "-" + currentMonth + "-" + currentDay;

    const currentTime = new Date();
    const currentHours = currentTime.getHours();
    const currentMinutes = currentTime.getMinutes();
    const currentSeconds = currentTime.getSeconds();
    newVideo.time = currentHours + ":" + currentMinutes + ":" + currentSeconds;


    const newVideoData = {
      id: Math.floor(Math.random() * 1000),
      title,
      url,
    };

    setVideos([newVideoData, ...videos]);
     setNewVideo({});
    setVideoTitle("");
    setVideoUrl("");
  };

  return (
    <div>

        <form onSubmit={handleAddVideo}>
          <div>
            <label htmlFor="title">Tile:</label>
            <input
              type="text"
              id="title"
              onChange={(e) => {
                setTitle(e.target.value);
              }}
            ></input>
          </div>
          <div>
            <label htmlFor="url">Url:</label>
            <input
              type="text"
              id="url"
              onChange={(e) => {
                setUrl(e.target.value);
              }}
              required
            ></input>
          </div>
          <div>
            <button type="button" onClick={handleShowValid}>
              Cancel
            </button>
            <button type="submit">Add</button>
          </div>
        </form>

    </div>
  );
}

export default AddVideo;
