import React from 'react';

const VideoItem = ({ video, onDelete, onVote }) => {



  const handleUpVote = () => {
    video.rating = video.rating + 1;
  };

  const handleDownVote = () => {
    video.rating = Math.max(video.rating - 1, 0);
  };

// const handleUpVote = () => {

//   setRating(rating + 1);
//   onVote(video.id, 'up');
// };

// const handleDownVote = () => {

//   setRating(rating - 1);
//   onVote(video.id, 'down');
// };


  const videoId = video.url.split('v=')[1];

  return (
    <div className="video-item">
      <iframe
        width="560"
        height="315"
        src={`https://www.youtube.com/embed/${videoId}`}
        title="YouTube video player"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
      ></iframe>
      <h5>{video.title}</h5>
      <p>Votes: {video.rating}</p>

      <button
        // onClick={handleUpVote(video.id)}
        type="button"
        style={{ fontSize: '24px' }}
        className="fa fa-thumbs-up"
      >
      </button>

      <button
        onClick={handleDownVote(video.id)}
        type="button"
        style={{ fontSize: '24px' }}
        className="fa fa-thumbs-down"
      >
      </button>

      <button
        onClick={() => onDelete(video.id)}
        type="button"
        className="btn btn-danger"
      >
        Delete
      </button>
    </div>
  );
};

export default VideoItem;
