import "./App.css";
import React, { useState } from "react";
// import data from '.data.json';
import Video from './Video';

function App() {

const [videos, setVideos] = useState(data);

function handleRemoveVideo(id) {
  setVideos(videos.filter((video) => video.id !== id));
}

function handleUpVote(id) {
  setVideos(
    videos.map((video) => {
      if (video.id === id) {
        return { ...video, rating: video.rating + 1 };
      }
      return video;
    })
  );
}

function handleDownVote(id) {
  setVideos(
    videos.map((video) => {
      if (video.id === id) {
        return { ...video, rating: Math.max(0, video.rating - 1) };
      }
      return video;
    })
  );
}



  return (
    <div className="App">
      {videos.map((video) => (
        <Video
          key={video.id}
          video={video}
          onRemove={() => handleRemoveVideo(video.id)}
          onUpVote={() => handleUpVote(video.id)}
          onDownVote={() => handleDownVote(video.id)}
        />
      ))}
      {/* <header className="App-header">
        <h1>Video Recommendation</h1>
      </header> */}
    </div>
  );
}

export default App;
