import { useState } from "react";

import exampleresponse from "../exampleresponse.json";

function VideoPlay() {
  const [vote, setVote] = useState(0);
  const voteIncrease = () => {
    setVote((prev) => prev + 1);
  };
  const voteDecrease = () => {
    setVote((prev) => {
      return prev > 0 ? prev - 1 : 0;
    });
  };

  return exampleresponse.map((el) => (
    <div class="container mt-5">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{el.title}</h5>
          <div class="embed-responsive embed-responsive-16by9">
            <video class="embed-responsive-item" controls>
              <source src={el.url} type="video" />
              Your browser does not support the video tag.
            </video>
          </div>
          <div className="">
            <button onClick={voteIncrease} type="button" class="btn btn-success"> + </button>
            <p>Vote: {vote}</p>
            <button onClick={voteDecrease} type="button" class="btn btn-danger">  - </button>
          </div>
        </div>
      </div>
    </div>
  ));
}

export default VideoPlay;
