import React, { useState } from "react";
import AddVideo from "./AddVideo";
import exampleresponse from "../exampleresponse.json";

function VideoPlay(videoId) {
  const [vote, setVote] = useState(0);
  const [vid, setVid] = useState(exampleresponse);

  const voteIncrease = () => {
    setVote((prev) => prev + 1);
  };

  const voteDecrease = () => {
    setVote((prev) => (prev > 0 ? prev - 1 : 0));
  };

  const newVideoAdd = (newInput) => {
    setVid([...vid, newInput]);
  };

  return (
    <div className="container mt-5">
      <AddVideo onVidAdd={newVideoAdd}  />

      {vid.map((el, index) => {
        //const videoId = getVideoIdFromUrl(el.url);

        return (
          <div className="videoCard" key={index}>
            <h5 className="card-title">{el.title}</h5>

            <iframe
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${videoId}`}
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>

            <div className="btnCounter">
              <button onClick={voteIncrease} type="button" className="btn btn-success">
                +
              </button>
              <p>Vote: {vote}</p>
              <button onClick={voteDecrease} type="button" className="btn btn-danger">
                -
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
}



export default VideoPlay;
