import { useState } from "react";
import exampleresponse from "../exampleresponse.json";

function Video({ videoObj }) {
  const videoId = getVideoIdFromUrl(videoObj.url);
  const [vote, setVote] = useState(videoObj.rating);
  const [videoDel, setVideoDel] = useState(videoObj)

  const voteIncrease = () => {
    setVote((prev) => prev + 1);
  };

  const voteDecrease = () => {
    setVote((prev) => (prev > 0 ? prev - 1 : 0));
  };

  // const deleteVideo = (videoId) => {
  //   const videoDeleted = videoDel.filter((el) => el.id !== videoId);
  //   setVideoDel(videoDeleted);

  // };


  const deleteVideo = (videoId) => {
    console.log("Deleting video with ID:", videoId);
    const videoDeleted = videoDel.filter((el) => el.id !== videoId);
    console.log("Updated videoDel array:", videoDeleted);
    setVideoDel(videoDeleted);
  };


  const date = new Date().toDateString();

  return (
    <div className="card">
      <iframe
        src={`https://www.youtube.com/embed/${videoId}`}
        frameBorder="0"
        allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
        title="YouTube video player"
        allowFullScreen
        height="300px"
      ></iframe>
      <div className="card-body">
        <h5 className="card-title">{videoObj.title}</h5>
        <p className="card-text">Requested At: {date}</p>
        <div className="buttons">
          <button onClick={voteIncrease} className="btn btn-primary">
            &#128077;
          </button>
          <p>{vote}</p>
          <button onClick={voteDecrease} className="btn btn-warning">
            &#128078;
          </button>
          <button onClick={()=>deleteVideo(videoObj.id)} className="btn btn-danger">
            <i className="fa fa-trash-o"></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Video;
function getVideoIdFromUrl(url) {
  const videoId = url.split("v=")[1];
  return videoId;
}
