import "./App.css";
import React, { useState } from 'react';

function Uploadvideo() {
////////////////////
const [title, setTitle] = useState('');
  const [url, setURL] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    if (title.trim() === '') {
      alert('Title cannot be empty.');
      return;
    }

    if (!isValidURL(url)) {
      alert('Please enter a valid URL.');
      return;
    }

    const currentDate = new Date();
    const formattedDate = currentDate.toISOString();

    const submission = {
      title: title,
      url: url,
      submittedAt: formattedDate
    };

    console.log(submission);

    // You can also perform further actions, like sending data to a server
  };

/////////////

const [showForm, setShowForm] = useState(false);

  const handleButtonClick = () => {
    if (!showForm) {
      setShowForm(true);
    }

  };
  const handleCancelButtonClick = () => {
    if (showForm) {
      setShowForm(false);
    }

  };


  return (

      <div>
        <button class="uploadVideo_btn" onClick={handleButtonClick}>

        <a href="#0">Upload Video</a>
      </button>
      {showForm && (
        <div class="container"> <form onSubmit={handleSubmit}id="form">
        <div>
          <label htmlFor="title">Title:</label>
          <input id="title"type="text" value={title} class="input" name="title" onChange={(e)=>setTitle(e.target.value)}required></input>
        </div>
        <div>
          <label htmlFor="url">url:&nbsp;&nbsp;&nbsp;</label>
          <input id="url" value={url}class="input" type="text"  name="url" onrequired></input>
        </div>
        <div class="formBtn">
          <button class="cancelBtn" onClick={handleCancelButtonClick}>Cancel</button>
          <button class="uploadBtn" type="submit">Upload</button>
        </div>
      </form></div>

      )}

        </div>

  );
}
function Searchvideo() {
  return (
    <div>
      <label for="searchid">Search:</label>
      <input id="searchid" type="text" class="searchVideo" name="search"></input>
    </div>
  );
}

export default function Search() {
  return (
    <div class="search">
      <Uploadvideo />
      <Searchvideo />
    </div>
  );
}
