import "./App.css";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React, { useState } from 'react';

function App() {
  const [isVisible, setIsVisible] = useState(false);

  const toggleDiv = () => {
    setIsVisible(!isVisible);
  };


  return (

    <div className="App">
      <header className="App-header">
        <h1>Video Recommendation</h1>
      </header>
      <Container>
      <Row>
      <Col>
      <Button variant="link" id="addvideo"
      onclick={toggleDiv}>add Video</Button>
      {isVisible && <div>This is the div that will be shown or hidden</div>}
      <div id="hidden">
      <InputGroup size="sm" className="mb-3">
        <InputGroup.Text id="inputGroup-sizing-sm">Title</InputGroup.Text>
        <Form.Control
          aria-label="Small"
          aria-describedby="inputGroup-sizing-sm"
        />
      </InputGroup>
      <InputGroup size="sm" className="mb-3">
      <InputGroup.Text id="inputGroup-sizing-sm">Url</InputGroup.Text>
      <Form.Control
        aria-label="Small"
        aria-describedby="inputGroup-sizing-sm"
      />
    </InputGroup>
    <br/>
    <Button variant="warning">Cancel</Button>
        <Button variant="danger">Add</Button>
        </div>
      </Col>
        <Col>
        <InputGroup size="sm" className="mb-3">
        <InputGroup.Text id="inputGroup-sizing-sm">Search</InputGroup.Text>
        <Form.Control
          aria-label="Small"
          aria-describedby="inputGroup-sizing-sm"
        />
      </InputGroup>

        </Col>

      </Row>

      </Container>
    </div>
  );
}

export default App;
