import React from "react";

function VideoComponent({ video, onUpVote, onDownVote, onRemove }) {
  return (
    <div className="video-component">
      <div className="video-container">
        <iframe src={video.url} title={video.title} allowFullScreen></iframe>
      </div>
      <h3 className="rating">Rating: {video.rating}</h3>
      <p>{video.title}</p>
      <div>
        <button className="up-vote" onClick={() => onUpVote(video.id)}>
          Up Vote
        </button>
        <button className="down-vote" onClick={() => onDownVote(video.id)}>
          Down Vote
        </button>
        <button onClick={() => onRemove(video.id)}>Remove</button>
      </div>
    </div>
  );
}

