import React, { useState } from 'react';

const AddVideoForm = ({ onAddVideo }) => {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title && url) {
      onAddVideo({ title, url, votes: 0 });
      setTitle('');
      setUrl('');
    }
  };

  return (
    <>
    <div className="add-video-form">
      <a href='0#' className='addVideo'>Add Video</a>
      <form onSubmit={handleSubmit}>
        <label>
          <span className='title'>Title</span>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </label>
        <label>
          <span className='url'>URL</span>
          <input type="url" value={url} onChange={(e) => setUrl(e.target.value)} />
        </label>
        <button className='btn btn-warning input' type="submit">Cancel</button>
        <button className='btn btn-danger input' type="submit">Add</button>
      </form>
      </div>
      <div className='search'>
      <form>
        <label>
          Search
          <input className='search-input' type="text" name="search"  />
        </label>
      </form>
      </div>
    </>
  );
};

export default AddVideoForm;
