import React, { useState } from 'react';

const VideoComponent = ({ videosData }) => {
  const [videos, setVideos] = useState(videosData);

  const handleVote = (id, ratingType) => {
    const updatedVideos = videos.map(video => {
      if (video.id === id) {
        return {
          ...video,
          votes: ratingType === 'up' ? video.rating + 1 : video.rating - 1,
        };
      }
      return video;
    });
    setVideos(updatedVideos);
  };

  const handleDeleteVideo = id => {
    const updatedVideos = videos.filter(video => video.id !== id);
    setVideos(updatedVideos);
  };

  return (
    <div>
      {videos.map(video => (
        <div key={video.id}>
          <h2>{video.title}</h2>
          {/* <video controls>
            <source src={video.url} type="video" />
            Your browser does not support the video tag.
          </video> */}

<div class="container .d-block d-md-none" style="width:95%;">

<iframe width="400" height="500"
  src="https://www.youtube.com/embed/{VIDEO_ID_GOES_HERE}" allow="autoplay; fullscreen;">
</iframe>

</div>
          <p>rating: {video.rating}</p>
          <button onClick={() => handleVote(video.id, 'up')}>Upvote</button>
          <button onClick={() => handleVote(video.id, 'down')}>Downvote</button>
          <button onClick={() => handleDeleteVideo(video.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default VideoComponent;
