// ShowingVideos.js
import React from "react";

function ShowingVideos({ result, selectedVideo }) {
  const convertToEmbedUrl = (url) => {
    const videoId = url.split("v=")[1];
    return `https://www.youtube.com/embed/${videoId}`;
  };

  const handleRating = (id, type) => {
    // Handle video rating here
    // ...
  };

  const sortedVideos = [...result].sort((a, b) => b.rating - a.rating);

  return (
    <div className="ShowingVideos">
      {selectedVideo && (
        <div className="selected-video">
          <h2>Selected Video</h2>
          <p>{selectedVideo.title}</p>
          <iframe
            className="allVideos"
            width="300"
            height="220"
            src={convertToEmbedUrl(selectedVideo.url)}
            title={selectedVideo.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      )}

      {sortedVideos.map((video) => (
        <div className="videos" key={video.id}>
          <p>{video.title}</p>
          <div className="buttons">
            <i
              className="fa-solid fa-thumbs-up"
              onClick={() => handleRating(video.id, "thumbs-up")}
            ></i>
            <h4>{video.rating}</h4>
            <i
              className="fa-solid fa-thumbs-down"
              onClick={() => handleRating(video.id, "thumbs-down")}
            ></i>
          </div>

          <iframe
            className="allVideos"
            width="300"
            height="220"
            src={convertToEmbedUrl(video.url)}
            title={video.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      ))}
    </div>
  );
}

export default ShowingVideos;
