import React, { useState } from "react";

function AddVideo({ onAddVideo }) {
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (title.trim() === "" || url.trim() === "") {
      alert("Please provide both a title and a URL.");
      return;
    }

    // Check if the URL is a valid YouTube URL
    if (!isValidYouTubeUrl(url)) {
      alert("Please provide a valid YouTube URL.");
      return;
    }

    const newVideo = {
      id: Date.now(), // You can use a unique ID generator
      title,
      url,
      rating: 0, // Initialize the rating to 0
    };

    // Call the onAddVideo function from the parent component
    onAddVideo(newVideo);

    // Clear the form fields
    setTitle("");
    setUrl("");
  };

  // Function to check if a URL is a valid YouTube URL
  const isValidYouTubeUrl = (url) => {
    // A simple check for YouTube video URLs
    return url.includes("youtube.com/watch?v=");
  };

  return (
    <div className="AddVideo">
      <h2>Add a YouTube Video</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="url">URL:</label>
          <input
            type="text"
            id="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
        </div>
        <button type="submit">Add Video</button>
      </form>
    </div>
  );
}

export default AddVideo;
