import React, { useState } from 'react';
import exampleData from '../exampleresponse.json';
import YouTubeVideo from './AddVideo';

const VideoList = () => {
  const [videos, setVideos] = useState(exampleData);

    const handleAddVideo = (newVideo) => {
    // Add the new video to the list
    setVideos([...videos, newVideo]);
  };

    const handleUpVote = (videoId) => {
    const updatedVideos = videos.map((video) =>
      video.id === videoId ? { ...video, rating: video.rating + 1 } : video
    );
    setVideos(updatedVideos);
  };

  const handleDownVote = (videoId) => {
    const updatedVideos = videos.map((video) =>
      video.id === videoId ? { ...video, rating: video.rating - 1 } : video
    );
    setVideos(updatedVideos);
  };

  const handleRemoveVideo = (videoId) => {
    // Filter out the video with the given ID
    const updatedVideos = videos.filter((video) => video.id !== videoId);
    setVideos(updatedVideos);
  };

  return (
    <div>
      {videos.map((video) => (
        <div key={video.id}>
          <h2>{video.title}</h2>
          <YouTubeVideo videoId={video.youtubeVideoId} />
          <p>Votes: {video.rating}</p>
          <button onClick={() => handleRemoveVideo(video.id)}>Remove Video</button>
          <button onClick={() => handleUpVote(video.id)}>Up Vote</button>
          <button onClick={() => handleDownVote(video.id)}>Down Vote</button>
        </div>
      ))}
    </div>
  );
};

export default VideoList;
