// AddVideo.js

import React, { useState } from 'react';

const AddVideo = ({ onAddVideo }) => {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const handleAddVideo = () => {
    // Generate a unique ID for the new video (you can use a library like `uuid`)
    const newVideo = {
      id: uuid(), // Generate a unique ID
      title,
      youtubeVideoId: getYouTubeVideoIdFromUrl(url), // Implement a function to extract video ID from URL
      votes: 0,
    };

    // Call the parent component's function to add the video
    onAddVideo(newVideo);

    // Reset the input fields
    setTitle('');
    setUrl('');
  };

  return (
    <div>
      <h2>Add a New Video</h2>
      <label>
        Title:
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
      </label>
      <label>
        URL:
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} />
      </label>
      <button onClick={handleAddVideo}>Add Video</button>
    </div>
  );
};

export default AddVideo;
