import React, { useState } from 'react';

const baseUrl = 'http://127.0.0.1:5000';
const videosEndpoint = `${baseUrl}/videos`;

const getYouTubeVideoIdFromUrl = (url) => {
  // Define patterns for different YouTube URL formats
  const patterns = [
    /(?:https?:\/\/)?(?:www\.)?youtube\.com\/watch\?v=([a-zA-Z0-9_-]+)/,
    /(?:https?:\/\/)?(?:www\.)?youtu\.be\/([a-zA-Z0-9_-]+)/,
  ];

  // Iterate through patterns and try to match with the given URL
  for (const pattern of patterns) {
    const match = url.match(pattern);
    if (match && match[1]) {
      return match[1]; // Return the video ID
    }
  }

  // If no match is found, return null
  return null;
};

const AddVideo = ({ onAddVideo }) => {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const handleAddVideo = () => {
    if (title.trim() === '' || !isValidYouTubeUrl(url)) {
      alert('Please enter a valid title and a valid YouTube URL.');
      return;
    }

    // Fetch ile POST isteği gönderme
    fetch(videosEndpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        title,
        youtubeVideoId: getYouTubeVideoIdFromUrl(url),
      }),
    })
      .then((response) => {
        if (response.status === 201) {
          // Veri başarıyla eklendi
          return response.json();
        } else {
          // Veri eklerken bir hata oluştu
          throw new Error('Failed to add video');
        }
      })
      .then((data) => {
        // Veri başarıyla eklenirse, onAddVideo fonksiyonunu çağırabilirsiniz
        onAddVideo(data);

        // Giriş alanlarını sıfırlayın
        setTitle('');
        setUrl('');
      })
      .catch((error) => {
        console.error('Error adding data:', error);
        // Hata durumunda kullanıcıya bir hata mesajı göstermek için uygun bir mekanizma ekleyin
        alert('An error occurred while adding the video.');
      });
  };

  const isValidYouTubeUrl = (url) => {
    // Add your validation logic here
    // For a simple example, you can check if the URL includes 'youtube.com'
    return url.includes('youtube.com');
  };

  return (
    <div className='addContainer'>
      <h2>Add your video</h2>
      <label>
        Title:
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
      </label>
      <label>
        YouTube URL:
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} />
      </label>
      <button onClick={handleAddVideo}>Add Video</button>
    </div>
  );
};

export default AddVideo;
