import React, { useEffect, useState } from "react";
import "./App.css";

//console.log(dataVideo)
function VideoComponent({ elements }) {
  return (
    <div class="container">
      <div class="videoDisplayContainer">
        {elements.map((element, index) => (
          <VideoDisplay key={index} element={element} />
        ))}
      </div>
    </div>
  );
}
function VideoDisplay({ element }) {
  const [score, setScore] = useState(element.rating);
  const [videoData, setVideoData] = useState([]);
  ////////////////////////////////////////
  useEffect(() => {
    fetch("/videos")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);
  useEffect(() => {
    fetch("/videos/:id")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleDelete = async (id) => {
    console.log("hi");
    try {
      const response = await fetch(`/videos/${id}`, { method: "DELETE" });
      if (response.ok) {
        setVideoData(videoData.filter((element) => element.id !== id));
      } else {
        console.error("Error deleting video:");
      }
    } catch (error) {
      console.error("Error deleting video:", error);
    }
  };

  return (
    <div class="displayGrid">
      <h5>{element.title}</h5>

      <iframe
        width="330"
        height="200"
        src={element.url}
        title={element.title}
        border="none"
        border-radius="103px"
        frameborder="0"
        allowfullscreen
        align="center"
      ></iframe>
      <div class="likedislikeBtn">
        <a
          href="#0"
          class="btn btn-info btn-lg"
          onClick={() => setScore((s) => s + 1)}
        >
          <span class="glyphicon glyphicon-thumbs-up"></span> Like
        </a>
        <p>Votes:&nbsp;{score}</p>

        <a href="#0" class="btn btn-info btn-lg">
          <span
            class="glyphicon glyphicon-thumbs-down"
            onClick={() => setScore((s) => s - 1)}
          ></span>
          Dislike
        </a>
      </div>
      <button class="deleteBtn" onClick={() => handleDelete(element.id)}>
        Delete
      </button>
    </div>
  );
}

export default function AllVideoDisplay() {
  const [videoData, setVideoData] = useState([]);

  useEffect(() => {
    fetch("/videos")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);
  useEffect(() => {
    fetch("/videos/:id")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <VideoComponent elements={videoData.sort((a, b) => b.rating - a.rating)} />
  );
}
