import React, { useEffect, useState } from "react";
import Video from "./Video";
import AddVideo from "./AddVideo";
//import exampleresponse from "../exampleresponse.json";
import "../index.css";

function VideosList() {

  //const videosList = exampleresponse;
  const [videos, setVideo] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/videos') // Update the URL to the correct backend endpoint
      .then((res) => res.json())
      .then((data) => {
        setVideo(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const newVideo = (newInput) => {
    newInput.id = video.length + 1;
    newInput.rating = 0;
    setVideo([...video, newInput]);
  };

  const deleteVideoItem = (videoId) => {
    const videoDeleted = video.filter((el) => el.id !== videoId);
    setVideo(videoDeleted);
  };

  const sortedVideos = [...video].sort((a, b) => b.rating - a.rating);
  return (
    <>
      <AddVideo onVidAdd={newVideo} />
      <div className="container mt-5">
        {sortedVideos.map((video) => (
          <Video videoObj={video} key={video.id} deleteVideo={deleteVideoItem} />
        ))}
      </div>
    </>
  );
}

export default VideosList;
