import React from 'react';

class Upvote extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            count: 0
        }
    }

    increment = () => {

        this.setState({
            count: this.state.count + 1
        })
    }

    render() {
        return (
            <div>
              <div>
                <i onClick={this.increment} style={{ fontSize: '24px'}} className="fa">&#xf087;</i>
                <span>{this.state.count}</span>
                </div>
            </div>
        );
    }
}

export default Upvote;
