import React from 'react';
import data from "../data/exampleresponse.json";
import { useState } from 'react';
import Cards from "./Cards"
const Form = ({form}) => {
    
 const [show,setShow]=useState(data); 
 const [add,setAdd]=useState("")
 

function showHandler(event){ 
  event.preventDefault();
 setAdd(show)
}
 function changeHandler(e){
const {name,value}=e.target;
const newObj={
    title:e.target.name,
    url:""
}
setShow(prevalue=>{
    return [...data,newObj]
 })
}
        
       

    return (
        <div>
            <form onSubmit={showHandler}  className="form-group" style={{display: form ? "inline" : "none"}}>
                <label >Title</label>
                <input   onChange={changeHandler} name="title" value={show.title} type="text"  placeholder="enter the title"  required></input>
                <br/>
                <label>URL</label>
                <input onChange={changeHandler} name="url" value={show.url}type="text" placeholder="enter the url" required ></input>
                <br/>
                <button  className="btn btn-success">ADD</button>
                <button  className="btn btn-danger">Cancel</button>
            </form>
            {data && data.map((card)=>{
                return (<Cards  key={card.id}card={card}/>)
            })}
            
        </div>
    );
}

export default Form;
