import { useRef, useState } from "react";


const Add=()=>{
    const title= useRef();
    const url=useRef();

    const [showStatus ,setShowStatus ] = useState("none")

    const openForm = ()=>{
        setShowStatus("flex")
    }

    const closeForm = ()=>{
        setShowStatus("none")
    }
    const saveVideo= ()=>{
        console.log(title.current.value, url.current.value)
    }
    return (
        <div className="form">
            <button onClick={openForm}> Add New Video </button>
       
            <div style={{display:showStatus}}>
                <h3>ADD YOUR VIDEO</h3> 
                <input type="text" ref={title} placeholder="Enter Your Ti"/>
                <input type="text" ref={url} />
              
                <div>
                    <button onClick={closeForm}> Cancle</button>
                    <button onClick={saveVideo} > Save </button>
                </div>
            </div>


         </div>

    )
}



 export default Add
