import React, { useState } from 'react';

const VideoComponent = ({ video, onRemoveVideo, onVote }) => {
  const [rating, setRating] = useState(video.rating);

  const handleUpVote = () => {
    setRating(rating + 1);
    onVote(video.id, 'up');
  };

  const handleDownVote = () => {
    setRating(rating - 1);
    onVote(video.id, 'down');
  };

  const handleRemove = () => {
    onRemoveVideo(video.id);
  };

  return (
    <div>
      <h3>{video.title}</h3>
      <iframe src={video.url} title={video.title}></iframe>
      <p>Number of Ratings: {rating}</p>
      <button onClick={handleUpVote}>Up Vote</button>
      <button onClick={handleDownVote}>Down Vote</button>
      <button onClick={handleRemove}>Remove Video</button>
    </div>
  );
};

export default VideoComponent;
