import React, { useState } from "react";
import "./App.css";

const Video = ({ video, handleVote, handleRemove }) => {
  const handleUpVote = () => {
    handleVote(video.id, "up");
  };

  const handleDownVote = () => {
    handleVote(video.id, "down");
  };

  const handleDelete = () => {
    handleRemove(video.id);
  };

  return (
    <div className="video">
      <h3>{video.title}</h3>
      <iframe
        title={video.title}
        width="560"
        height="315"
        src={video.url}
        frameBorder="0"
        allowFullScreen
      ></iframe>
      <div className="video-controls">
        <button onClick={handleUpVote}>Up</button>
        <button onClick={handleDownVote}>Down</button>
        <button onClick={handleDelete}>Remove</button>
      </div>
    </div>
  );
};

const VideoList = ({ videos, handleVote, handleRemove }) => {
  return (
    <div className="video-list">
      <h2>Video List</h2>
      {videos.map((video) => (
        <Video
          key={video.id}
          video={video}
          handleVote={handleVote}
          handleRemove={handleRemove}
        />
      ))}
    </div>
  );
};

const App = () => {
  const [videos, setVideos] = useState([
    {
      id: 1,
      title: "Video 1",
      url: "https://www.youtube.com/embed/VIDEO_ID_1",
      votes: 0,
    },
    {
      id: 2,
      title: "Video 2",
      url: "https://www.youtube.com/embed/VIDEO_ID_2",
      votes: 0,
    },
  ]);

  const handleVote = (videoId, type) => {
    setVideos((prevVideos) =>
      prevVideos.map((video) => {
        if (video.id === videoId) {
          if (type === "up") {
            return { ...video, votes: video.votes + 1 };
          } else if (type === "down") {
            return { ...video, votes: video.votes - 1 };
          }
        }
        return video;
      })
    );
  };

  const handleRemove = (videoId) => {
    setVideos((prevVideos) =>
      prevVideos.filter((video) => video.id !== videoId)
    );
  };

  const handleAddVideo = () => {
    const newVideoId = videos.length + 1;
    const newVideo = {
      id: newVideoId,
      title: `Video ${newVideoId}`,
      url: "https://www.youtube.com/embed/NEW_VIDEO_ID",
      votes: 0,
    };
    setVideos((prevVideos) => [...prevVideos, newVideo]);
  };

  return (
    <div className="app">
      <h1>Video Player</h1>
      <button onClick={handleAddVideo}>Add Video</button>
      <VideoList
        videos={videos}
        handleVote={handleVote}
        handleRemove={handleRemove}
      />
    </div>
  );
};

export default App;
