import React, { useState } from 'react';
import './ModifiedVideo.css';
import axios from 'axios';

function convertWatchToEmbedLink(watchLink) {
  const watchPattern = /https:\/\/www\.youtube\.com\/watch\?v=([A-Za-z0-9_-]+)/;
  const match = watchLink.match(watchPattern);
  if (match && match.length === 2) {
    const videoId = match[1];
    const embedLink = `https://www.youtube.com/embed/${videoId}`;
    return embedLink;
  } else {
    return watchLink;
  }
}

function ModifiedVideo({ video, onRemove }) {
  const [rating, setRating] = useState(video.rating);

  const handleUpVote = () => {
    setRating(rating + 1);
  };

  const handleDownVote = () => {
    setRating(rating - 1);
  };

  const videoUrl = video.url && video.url.startsWith('https://www.youtube.com/watch?v=')
    ? convertWatchToEmbedLink(video.url)
    : video.url;

  return (
    <div className="modified-video-container">
      <h2>{video.title}</h2>
      <div className="modified-video-embed">
        <iframe
          width="560"
          height="315"
          src={videoUrl}
          title={video.title}
          allowFullScreen
        />
      </div>
      <p>Rating: {rating}</p>
      <button className="modified-remove-button" onClick={() => onRemove(video.id)}>
        Remove Video
      </button>
      <button className="modified-upvote-button" onClick={handleUpVote}>
        Up Vote
      </button>
      <button className="modified-downvote-button" onClick={handleDownVote}>
        Down Vote
      </button>
    </div>
  );
}

export default ModifiedVideo;
