import { useState } from "react";

function Addvideo() {
  const AllVideos = [];

  const [videos, setVideos] = useState(AllVideos);
  const [newVideo, setNewVideo] = useState({ title: "", url: "" });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewVideo({ ...newVideo, [name]: value });
  };

  const addVideo = () => {
    if (newVideo.title && newVideo.url) {
      setVideos([...videos, { ...newVideo, rating: 0 }]);
      setNewVideo({ title: "", url: "" });
    }
  };

  const upvoteVideo = (id) => {
    const updatedVideos = videos.map((video) =>
      video.id === id ? { ...video, rating: video.rating + 1 } : video
    );
    setVideos(updatedVideos);
  };

  const downvoteVideo = (id) => {
    const updatedVideos = videos.map((video) =>
      video.id === id ? { ...video, rating: video.rating - 1 } : video
    );
    setVideos(updatedVideos);
  };

  const removeVideo = (id) => {
    const updatedVideos = videos.filter((video) => video.id !== id);
    setVideos(updatedVideos);
  };
  return (
    <>
      <div>
        <h2>Add a Video</h2>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newVideo.title}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="url"
          placeholder="URL"
          value={newVideo.url}
          onChange={handleInputChange}
        />
        <button onClick={addVideo}>Add Video</button>
      </div>
      <div>
        <h2>Videos</h2>
        <ul className="ShowingVideos">
          {videos.map((video) => (
            <div className="videos">
              <li key={video.id}>
                <h3>{video.title}</h3>
                <iframe
                  className="allVideos"
                  title={video.title}
                  width="560"
                  height="315"
                  src={`https://www.youtube.com/embed/${getYouTubeVideoId(
                    video.url
                  )}`}
                  frameBorder="0"
                  allowFullScreen
                ></iframe>

                {/* <p>Rating: {video.rating}</p>
                <button onClick={() => upvoteVideo(video.id)}>{}</button>
                <button onClick={() => downvoteVideo(video.id)}>
                  Down Vote
                </button>
                <button onClick={() => removeVideo(video.id)}>Delete</button> */}

                <div className="buttons">
                  <i
                    className="fa-solid fa-thumbs-up"
                    onClick={() => upvoteVideo(video.id, "thumbs-up")}
                  ></i>
                  <h4>{video.rating}</h4>
                  <i
                    className="fa-solid fa-thumbs-down"
                    onClick={() => downvoteVideo(video.id, "thumbs-down")}
                  ></i>
                </div>
              </li>
            </div>
          ))}
        </ul>
      </div>
    </>
  );
}
function getYouTubeVideoId(url) {
  const videoIdMatch = url.match(/(?:\/|v=)([A-Za-z0-9_-]{11})(?=&|$)/);
  if (videoIdMatch) {
    return videoIdMatch[1];
  }
  return "";
}

export default Addvideo;
