import React , {useState} from "react";
import exampleresponse from "../exampleresponse.json";


function Video(prop) {
  const videoId = getVideoIdFromUrl(prop.videoObj.url);
  const [vid, setVid] = useState(exampleresponse);


  //const voteIncrease = () => {
  //     setVote((prev) => prev + 1);
  //   };

  //   const voteDecrease = () => {
  //     setVote((prev) => (prev > 0 ? prev - 1 : 0));
  //   };

  const updateVoteCount = (videoId, value) => {
    setVid((prevVid) =>
      prevVid.map((video) => (video.id === videoId ? { ...video, votes: (video.votes || 0) + value } : video))
    );
  };

  const newVideoAdd = (newInput) => {
    const newId = vid.length + 1;
    newInput.id = newId;
    newInput.votes = 0;
    setVid([...vid, newInput]);
  };

  const deleteVideo = (videoId) => {
    const videoDeleted = vid.filter((el) => el.id !== videoId);
    setVid(videoDeleted);
  };

  let date = new Date().toDateString();

  let sortedVideos = [...vid].sort((a, b) => b.votes - a.votes);



  return (

    <div className="card">
      <iframe
        src={`https://www.youtube.com/embed/${videoId}`}
        frameborder="0"
        allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
        title="YouTube video player"
        allowfullscreen height={'300px'}
      ></iframe>
      <div className="card-body">
        <h5 className="card-title">{prop.videoObj.title}</h5>
        <p className="card-text">Requasted At :</p>
        <div className="buttons">
          <button onclassName="btn btn-primary"> &#128077; </button>
          <p>{prop.videoObj.rating} </p>
          <button onclassName="btn btn-warning"> &#128078; </button>
          <button onclassName="btn btn-danger">
            <i className="fa fa-trash-o"></i>
          </button>
        </div>
      </div>
    </div>
  );
}
export default Video;
function getVideoIdFromUrl(url) {
  const videoId = url.split("v=")[1];
  return videoId;
}
