import "./App.css";
import React, { useState } from 'react';
import AddVideoForm from './AddVideoForm';
import videosData from './videosData'; // Import your local video data

function App({ video, onUpVote, onDownVote, onDelete }) {
  // const [videos, setVideos] = useState(videosData);
  // const [UpVote, setUpVote] = useState(0);
  // const [DownVote, setDownVote] = useState(0);

  // const handleUpVote = (e) => setUpVote(UpVote + 1)

  // const handleDownVote = (e) => setDownVote(DownVote - 1);

  // const handleDelete = (video) => {
  //   const updatedVideos = videos.filter((v) => v.id !== video.id);
  //   setVideos(updatedVideos);
  // };

  // const handleAddVideo = (newVideo) => {
  //   setVideos([...videos, newVideo]);
  // };

  const [videos, setVideos] = useState(videosData);



  const handleVote = (id, ratingType) => {
    const updatedVideos = videos.map(video => {
      if (video.id === id) {
        return {
          ...video,
          votes: ratingType === 'up' ? video.rating + 1 : video.rating - 1,
        };
      }
      return video;
    });
    setVideos(updatedVideos);
  };

  const handleDeleteVideo = id => {
    const updatedVideos = videos.filter(video => video.id !== id);
    setVideos(updatedVideos);
  };


  return (
    <div className="App">
      <header className="App-header">
        <h4>Video Recommendation</h4>
      </header>
      <AddVideoForm onAddVideo={handleAddVideo} />
      {/* <button onClick={handleUpVote}>
        <i className="fas fa-thumbs-up"></i> Up Vote
      </button> */}
             <i onClick={() => handleVote(video)} style={{ fontSize: '24px'}} className="fa">&#xf087;</i>
      {/* <button onClick={handleDownVote}>
        <i className="fas fa-thumbs-down"></i> Down Vote
      </button> */}
        <i onClick={() => handleVote(video)} style={{ fontSize: '24px' }} className="fa">&#xf088;</i>
      <button className='del' onClick={handleDeleteVideo}>Delete</button>
    </div>
  );
}

export default App;
