import React, { useState } from 'react';
import VideoItem from './VideoItem';
import AddVideoForm from './AddVideoForm';

const AllButtons = () => {
  const [videos, setVideos] = useState([]);

  // const handleUpVote = (videoToUpdate) => {

  //   // Update the vote count for the video
  //   const updatedVideosUpVote = videos.map((video) =>
  //     video.id === videoToUpdate.id
  //       ? { ...video, votes: video.votes + 1 }
  //       : video
  //   );
  //   setVideos(updatedVideosUpVote);
  // };

  // const handleDownVote = (videoToUpdate) => {
  //   // Update the vote count for the video
  //   const updatedVideosDownVote = videos.map((video) =>
  //     video.id === videoToUpdate.id
  //       ? { ...video, votes: Math.max(video.votes - 1, 0) }
  //       : video
  //   );
  //   setVideos(updatedVideosDownVote);
  // };

  const handleDelete = (videoToDelete) => {
    // Remove the video from the list
    const updatedVideos = videos.filter((video) => video.id !== videoToDelete.id);
    setVideos(updatedVideos);
  };

  const handleAddVideo = (newVideo) => {
    // Add a new video to the list
    const updatedVideos = [...videos, newVideo];
    setVideos(updatedVideos);
  };

  return (
      <div className="todo-list">
      <h1>Video App</h1>
    <AddVideoForm onAddVideo={handleAddVideo} />
    <div className="video-list">
        {videos.map((video) => (
          <VideoItem
            key={video.id}
            video={video}
            // onUpVote={handleUpVote}
            // onDownVote={handleDownVote}
            onDelete={handleDelete} />
        ))}

      </div>  
    </div>
  );
};

export default AllButtons;
