import React, { useState, useEffect } from 'react';
import './App.css';
import VideoList from './VideoList';
import AddVideo from './AddVideo';

function App() {
  const [videos, setVideos] = useState([]);

  // Load video data from exampleresponse.json (you may need to use fetch or axios)
  useEffect(() => {
    // Fetch data from JSON file and set it in the state
    // Replace with actual data loading code
    const exampleResponse = require('./exampleresponse.json');
    setVideos(exampleResponse);

    // Sort the videos by votes in descending order
    sortVideos(exampleResponse);
  }, []);

  // Handle upvotes and downvotes
  const handleVote = (video, type) => {
    const updatedVideos = [...videos];
    const index = updatedVideos.findIndex((v) => v.id === video.id);

    if (type === 'up') {
      updatedVideos[index].votes++;
    } else if (type === 'down') {
      updatedVideos[index].votes--;
    }

    sortVideos(updatedVideos);
  };

  // Handle video deletion
  const handleDelete = (video) => {
    const updatedVideos = videos.filter((v) => v.id !== video.id);
    setVideos(updatedVideos);
  };

  // Handle adding a new video
  const handleAddVideo = (video) => {
    const updatedVideos = [...videos, video];
    sortVideos(updatedVideos);
  };

  // Sort videos by votes in descending order
  const sortVideos = (videoArray) => {
    videoArray.sort((a, b) => b.votes - a.votes);
    setVideos(videoArray);
  };

  return (
    <div className="App">
      <h1>Video App</h1>
      <AddVideo onAddVideo={handleAddVideo} />
      <VideoList videos={videos} onVote={handleVote} onDelete={handleDelete} />
    </div>
  );
}

export default App;
