import React, { useState } from 'react';

const AddVideoForm = ({ onAddVideo }) => {
  const [ inputValue, setInputValue ] = useState("Value from onChanges");
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const handleCancel = () => {
    setInputValue("");
};

const handleSave=(event) =>{
  console.log(inputValue, event);
}


  const handleSubmit = (e) => {
    e.preventDefault();
    if (title && url) {
      onAddVideo({ title, url });
      setTitle('');
      setUrl('');
    }
  };


  return (
    <div className="add-video-form">
      <a href = '#0' onClick={handleSubmit} >Add a Video</a>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          {/* <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </label> */}
        <input type="text" value= {inputValue}  onChange={(e) => setInputValue(e.target.value)}></input></label>
        <label>
          URL:
          <input type="url" value={url} onChange={(e) => setUrl(e.target.value)} />
        </label>
        <button type="submit" className='btn btn-warning' onClick={handleCancel}>Cancel</button>
        <button type="submit" className='btn btn-danger'>Add</button>
      </form>
       <form>
         <label>
           Search
           <input className='search-input' type="text" name="search" />
         </label>
       </form>
    </div>
  );
};

export default AddVideoForm;
