import React, { useEffect, useState } from "react";
import VideoList from "./components/VideoList";
import AddVideoForm from "./components/AddVideoForm";

const App = () => {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/") // Replace with your backend API URL
      .then((response) => response.json())
      .then((data) => {
        setVideos(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleAddVideo = (title, url) => {
    const video = {
      title: title,
      url: url,
    };

    fetch("http://127.0.0.1:5000/", { // Replace with your backend API URL
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(video),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.id) {
          // Video added successfully
          const newVideo = { id: data.id, title: title, url: url, votes: 0 };
          setVideos([...videos, newVideo]);
        } else {
          // Failed to add video
          console.error(data.message);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleVote = (videoId, increment) => {
    setVideos((prevVideos) =>
      prevVideos.map((video) =>
        video.id === videoId ? { ...video, votes: video.votes + increment } : video
      )
    );
  };

  const handleRemoveVideo = (videoId) => {
    fetch(`http://127.0.0.1:5000/${videoId}`, { // Replace with your backend API URL
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          // Video removed successfully
          setVideos((prevVideos) =>
            prevVideos.filter((video) => video.id !== videoId)
          );
        } else {
          // Failed to remove video
          console.error(data.message);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Video Website</h1>
      <VideoList videos={videos} handleVote={handleVote} handleRemove={handleRemoveVideo} />
      <AddVideoForm handleAddVideo={handleAddVideo} />
    </div>
  );
};

export default App;
