import React, { useState } from 'react';
import VideoList from './VideoList';
import AddVideoForm from './AddVideoForm';
import videosData from './videosData'; // Import your local video data

function App() {
  const [videos, setVideos] = useState(videosData);
  const [UpVote, setUpVote] = useState(0);
  const [DownVote, setDownVote] = useState(0);

  // const handleUpVote = (video) => {
  //   const updatedVideos = videos.map((v) =>
  //     v.id === video.id ? { ...v, votes: v.votes + 1 } : v
  //   );
  //   setVideos(updatedVideos);
  // };

  const handleUpVote = (e) => setUpVote(UpVote )

  const handleDownVote = (video) => {
    const updatedVideos = videos.map((v) =>
      v.id === video.id ? { ...v, votes: v.votes - 1 } : v
    );
    setVideos(updatedVideos);
  };

  const handleDelete = (video) => {
    const updatedVideos = videos.filter((v) => v.id !== video.id);
    setVideos(updatedVideos);
  };

  const handleAddVideo = (newVideo) => {
    setVideos([...videos, newVideo]);
  };

  return (
    <div className="app">
      <h1>Video App</h1>
      <AddVideoForm onAddVideo={handleAddVideo} />
      <VideoList
        videos={videos}
        onUpVote={handleUpVote}
        onDownVote={handleDownVote}
        onDelete={handleDelete}
      />
    </div>
  );
}

export default App;
