// Video.js

import React from "react";

const Video = ({ id, title, url, votes, handleVote, handleRemoveVideo }) => {
  const handleVoteClick = (increment) => {
    handleVote(id, increment);
  };

  const handleRemoveClick = () => {
    handleRemoveVideo(id);
  };

  return (
    <div className="video">
      <iframe
        title={title}
        width="560"
        height="315"
        src={url}
        frameBorder="0"
        allow="autoplay; encrypted-media"
        allowFullScreen
      ></iframe>
      <div className="video-details">
        <h3>{title}</h3>
        <div className="vote-buttons">
          <button className="vote-button" onClick={() => handleVoteClick(1)}>
            Up
          </button>
          <span>{votes}</span>
          <button className="vote-button" onClick={() => handleVoteClick(-1)}>
            Down
          </button>
        </div>
        <button className="remove-button" onClick={handleRemoveClick}>
          Remove
        </button>
      </div>
    </div>
  );
};

export default Video;
