import React, {useS} from 'react';
import VideoComponent from './VideoComponent';
import videosData from './videosData.json';
import AddVideoComponent from './AddVideoComponent';

function App() {
  const [videos, setVideos] = useState(videosData);

  const handleVideoAdd = newVideo => {
    const updatedVideos = [...videos, { ...newVideo, id: videos.length + 1, votes: 0 }];
    setVideos(updatedVideos);
  };


  return (
    <div className="App">
      <h1>Video App</h1>
      <AddVideoComponent onVideoAdd={handleVideoAdd} />
      <VideoComponent videosData={videosData} />
    </div>
  );
}

export default App;
