import "./App.css";
import React, { useState } from 'react';
import AddVideoForm from './AddVideoForm';
import videosData from './videosData';
import VideoList from './VideoList';



function App({ video, onUpVote, onDownVote, onDelete }) {
  //const [deletedVideos, setDeletedVideos] = useState([]);

  const [videos, setVideos] = useState(videosData); 

  const handleVote = (id, type) => {
    setVideos((prevVideos) =>
      prevVideos.map((video) => {
        if (video.id === id) {
          return {
            ...video,
            rating: type === "up" ? video.rating + 1 : video.rating - 1,
          };
        }
        return video;
      })
    );
  };

  const handleDelete = (id) => {
    setVideos((prevVideos) => prevVideos.filter((video) => video.id !== id));
      // // Filter out deleted videos from the original data
  const videos = videosData.filter((video) => !deletedVideos.includes(video.id));
  };

  const handleAdd = (newVideo) => {
    setVideos((prevVideos) => [
      ...prevVideos,
      { ...newVideo, id: Date.now(), rating: 0 },
    ]);
  };



  // const handleDelete = (videoId) => {
  //   // Update the deleted videos list
  //   setDeletedVideos((prevDeletedVideos) => [...prevDeletedVideos, videoId]);
  // };
  // // Filter out deleted videos from the original data
  // const videosToShow = videosData.filter((video) => !deletedVideos.includes(video.id));

  return (
    <div className="App">
      <header className="App-header">
        <h4>Video Recommendation</h4>
      </header>
      <AddVideoForm onAddVideo={handleAdd}/>
      <VideoList videos={videos} onVote={handleVote} onDelete={handleDelete} />
    </div>
  );
}

export default App;
