import React, { useState } from 'react';

const VideoComponent = ({ videosData }) => {
  const [videos, setVideos] = useState(videosData);

  const handleVote = (id, ratingType) => {
    const updatedVideos = videos.map(video => {
      if (video.id === id) {
        return {
          ...video,
          votes: Type === 'up' ? video.votes + 1 : video.votes - 1,
        };
      }
      return video;
    });
    setVideos(updatedVideos);
  };

  const handleRemoveVideo = id => {
    const updatedVideos = videos.filter(video => video.id !== id);
    setVideos(updatedVideos);
  };

  return (
    <div>
      {videos.map(video => (
        <div key={video.id}>
          <h2>{video.title}</h2>
          <video controls>
            <source src={video.url} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
          <p>Votes: {video.rating}</p>
          <button onClick={() => handleVote(video.id, 'up')}>Upvote</button>
          <button onClick={() => handleVote(video.id, 'down')}>Downvote</button>
          <button onClick={() => handleRemoveVideo(video.id)}>Remove Video</button>
        </div>
      ))}
    </div>
  );
};

export default VideoComponent;
