import React from 'react';
import data from "../data/exampleresponse.json";
import { useState } from 'react';
import Card from "./Card"
const Form = ({form}) => {
    const [show,setShow]=useState();

function showHandler(event){ 
  event.preventDefault()
}
function handlChange(e){
    const event=e.target.value
    const find= data.filter(item=>
        item.title.toLocaleLowerCase().includes(event.toLocaleLowerCase())
       )
       setShow(find)    
}
    return (
        <div>
            <form  onSubmit={showHandler}className="form-group" style={{display: form ? "inline" : "none"}}>
                <label>Title</label>
                <input onChange={handlChange} type="text"  placeholder="enter the title"  ></input>
                <br/>
                <lable>URL</lable>
                <input type="text" placeholder="enter the url" name={URL} ></input>
                <br/>
                <button  className="btn btn-success">ADD</button>
                <button  className="btn btn-danger">Cancel</button>
            </form>
            <Card show={show}/>
            
        </div>
    );
}

export default Form;
