// import Joke from './Joke'
import { useState } from "react"
import AddvideoForm from './AddvideoForm'
import Allvideos from "./Allvideos"

function App() {
  const [jokes, setJokes] = useState(Allvideos)

  const handleAddVideo = (text) => {
    const video = {
      text,
      id: self.crypto.randomUUID(),
      likes: 0
    }
    // jokes.push(joke)
    setJokes([joke, ...jokes])
    console.log("New Joke:", text)
  }

  const handleDeleteJoke = (id) => {
    setJokes(jokes.filter(joke => joke.id !== id))
    console.log("delete joke", id)
  }

  const handleLike = (id) => {
    setJokes(jokes.map(joke => {
      if (joke.id === id) {
        return {...joke, likes: joke.likes + 1}
      } else {
        return joke
      }
    }))
  }

  const handleDislike = (id) => {
    setJokes(jokes.map(joke => {
      if (joke.id === id) {
        return {...joke, likes: joke.likes - 1}
      } else {
        return joke
      }
    }))
  }

  const handleSort = () => {
    setJokes([...jokes].sort((a, b) => b.likes - a.likes))
  }

  return (
    <div className="">
      <h1>Dad Jokes</h1>

      <button onClick={handleSort}>Sort</button>

      <AddvideoForm onAddJoke={handleAddJoke} />

      {jokes.map(joke => (
        <Joke 
        onDelete={handleDeleteJoke} 
        key={joke.id} 
        onLike={handleLike}
        onDislike={handleDislike}
        {...joke} />
      ))}


    </div>
  )
}

export default App
