import { useState } from "react";

function Video({ videoObj, deleteVideo }) {
  const videoId = getVideoIdFromUrl(videoObj.url);
  const [vote, setVote] = useState(videoObj.rating);

  // const voteIncrease = () => {
  //   fetch("http://127.0.0.1:5050/videos",{
  //     method:'POST',
  //     headers :{
  //       'Content-Type': 'application/json',
  //       }, body:{'id': `${videoId}`, 'vote':'up'}

  //       })
  //       .then((res)=>res.json())
  //       .then(()=>{
  //         setVote(vote + 1)
  //       })
  //     }
  // const voteIncrease = () => {
  //   fetch("http://127.0.0.1:5050/videos/:id", {
  //     method: 'PUT',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ id: videoId, action: 'up' }),
  //   })
  //     .then((res) => res.json())
  //     .then(() => {
  //       setVote(vote + 1);
  //     });
  // };

  const voteIncrease = () => {
    const newRating = videoObj.rating + 1;
    setButtonDisabled(true);

    fetch(`http://127.0.0.1:5050/videos/${videoObj.id}`, { // Use backticks to include the video ID
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ newRating}), // You don't need to send the video ID here
    })
      .then((res) => res.json())
      .then(() => {
        setVote(vote + 1);
        setButtonDisabled(false);
      });
  };



  const voteDecrease = () => {
    setVote(() => (vote > 0 ? vote - 1 : 0));
  };

  const date = new Date().toDateString();

  return (
    <div className="card">
      <iframe
        src={`https://www.youtube.com/embed/${videoId}`}
        frameBorder="0"
        allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
        title="YouTube video player"
        allowFullScreen
        height="300px"
      ></iframe>
      <div className="card-body">
        <h5 className="card-title">{videoObj.title}</h5>
        <p className="card-text">Requested At: {date}</p>
        <div className="buttons">
          <button onClick={voteIncrease} className="btn btn-primary">
            &#128077;
          </button>
          <p>{vote}</p>
          <button onClick={voteDecrease} className="btn btn-warning">
            &#128078;
          </button>
          <button onClick={() => deleteVideo(videoObj.id)} className="btn btn-danger">
            <i className="fa fa-trash-o"></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Video;
