function getVideoIdFromUrl(url) {
  if (url && typeof url === "string" && url.includes("v=")) {
    const videoId = url.split("v=")[1];
    return videoId;
  } else {
    // Handle the case where url is null, undefined, empty, or doesn't contain "v="
    console.error("Invalid URL:", url);
    return null; // or return an appropriate default value or error handling
  }
}
