import React, { useState } from "react";
import { Header, Forms } from "./components/index.js";


function App() {

let [addVideo, setAddVideo] = useState(false)
const showNewSection = () => {
  if(addVideo === true){
    setAddVideo = false;
  }else{
    setAddVideo = true
  }

}

  return (
    <div>
     <Header/>
     <p><a href="" onClick={showNewSection}>Add Video</a></p>
     <Forms forms={addVideo}/>
    </div>
   
  );
}

export default App;
