import React, { useState, useEffect } from "react";
import LikeButton from "./LikeButton";
import EmbedVideo from "./EmbedVideo"
import axios from "axios";

function VideosForm(props) {
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");
  const [rating, setRating] = useState("");
  const [videoList, setVideoList] = useState([]);
  const [link, setLink] = useState("");
  // const [stateRating, setStateRating] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3001/api/get").then((response) => {
      setVideoList(response.data.rows);
    });
  }, []);

  function handleSubmit(e) {
    console.log(title)
    console.log(url)
    if (title && url) {
      axios.post("http://localhost:3001/api/insert", {
        title: title,
        url: url,
        rating: rating,

      });
    } else {
      alert("Please enter ")
    }

    e.preventDefault();

    setVideoList([
      ...videoList,
      { newTitle: title, newUrl: url, newRating: rating },
    ]);
  }

  function handleDelete(vid) {
    axios.delete(`http://localhost:3001/api/delete/${vid}`);
  }

  return (
    <div className="videos">
    <div className="form">
    <p>Add more videos:</p>
      <input className="video-input"
        type="text"
        placeholder="Enter Video Title"
        id="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />

      <input className="video-input"
        type="url"
        placeholder="Enter Video Link"
        id="text"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
      />

      <input className="video-input"
        type="number"
        placeholder="Enter Rating"
        id="text"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
      />
      <button className="video-input" onClick={handleSubmit}>Add Video</button>
</div>


      {videoList.map((video, i) => (

        <div className="flex-container" key={i}>
         <p>{video.title}</p> 
         <EmbedVideo video={video} />

         {/* <iframe
        src={video.url.replace("watch?v=", "embed/")}
        title="YouTube video player"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
        width="560"
        height="315"
      ></iframe> */}

         <h3> Rating: {video.rating}</h3> 

          <LikeButton key={video.id} id={video.id} /> 

          <button
            onClick={() => {
              handleDelete(video.id);
            }}
          >
            Delete
          </button> 
        </div>
      ))}
    </div>
  );
}

