import React, { useState } from 'react';

const baseUrl = 'http://localhost:5000';
const videosEndpoint = `${baseUrl}/videos`;

const getYouTubeVideoIdFromUrl = (url) => {
  const patterns = [
    /(?:https?:\/\/)?(?:www\.)?youtube\.com\/watch\?v=([a-zA-Z0-9_-]+)/,
    /(?:https?:\/\/)?(?:www\.)?youtu\.be\/([a-zA-Z0-9_-]+)/,
  ];

  for (const pattern of patterns) {
    const match = url.match(pattern);
    if (match && match[1]) {
      return match[1]; 
    }
  }

  return null;
};

const AddVideo = ({ onAddVideo }) => {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const handleAddVideo = () => {
    if (title.trim() === '' || !isValidYouTubeUrl(url)) {
      alert('Lütfen geçerli bir başlık ve geçerli bir YouTube URL\'si girin.');
      return;
    }

    // POST isteği göndermek için fetch kullanma
    fetch(videosEndpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        title,
        youtubeVideoId: getYouTubeVideoIdFromUrl(url),
      }),
    })
      .then((response) => {
        if (response.status === 201) {
          // Veri başarıyla eklendi
          return response.json();
        } else {
          // Veri eklerken bir hata oluştu
          throw new Error('Video ekleme hatası');
        }
      })
      .then((data) => {
        // Veri başarıyla eklenirse, onAddVideo fonksiyonunu çağırabiliriz
        onAddVideo(data);

        // Giriş alanlarını sıfırla
        setTitle('');
        setUrl('');
      })
      .catch((error) => {
        console.error('Veri eklerken hata oluştu:', error);
        // Hata durumunda kullanıcıya bir hata mesajı göstermek için uygun bir mekanizma ekleyin
        alert('Video eklenirken bir hata oluştu.');
      });
  };

  const isValidYouTubeUrl = (url) => {
    // Burada geçerlilik kontrol mantığını ekleyin
    // Basit bir örnek olarak, URL'nin 'youtube.com' içerip içermediğini kontrol edebilirsiniz
    return url.includes('youtube.com');
  };

  return (
    <div className='addContainer'>
      <h2>Video ekleyin</h2>
      <label>
        Title:
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
      </label>
      <label>
        YouTube URL:
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} />
      </label>
      <button onClick={handleAddVideo}>Video Ekle</button>
    </div>
  );
};

export default AddVideo;
